function createfigure2(X1, Y1, Y2)
%CREATEFIGURE2(X1, Y1, Y2)
%  X1:  vector of x data
%  Y1:  vector of y data
%  Y2:  vector of y data

%  Auto-generated by MATLAB on 14-Jul-2015 17:49:57
day = '23 Mar 2015';

% Create figure
figure1 = figure;

% Create axes
axes1 = axes('Parent',figure1,'YTick',[300 350 400],'YColor',[0 0 1],...
    'XTickLabel',{'0','15','30','45','60'},...
    'XTick',[0 90 180 270 360],...
    'FontSize',18,...
    'FontName','arial narrow');
%% Uncomment the following line to preserve the X-limits of the axes
xlim(axes1,[0 360]);
%% Uncomment the following line to preserve the Y-limits of the axes
ylim(axes1,[300 400]);

box(axes1,'on');
hold(axes1,'all');

% Create plot
plot(X1,Y1,'Parent',axes1,'LineWidth',2);

% Create title
title('G_{DNI} and Inlet Temperature','FontSize',20,...
    'FontName','Arial Narrow');

% Create ylabel
ylabel('G_{DNI}','FontSize',20,'FontName','Arial Narrow','Color',[0 0 1]);

% Create xlabel
xlabel('Time (minutes)','FontSize',20,'FontName','Arial Narrow');

% Create axes
axes2 = axes('Parent',figure1,'YTick',[50 60 70 80 90 100],...
    'YAxisLocation','right',...
    'YColor',[0 0.5 0],...
    'XTickLabel',{'0','15','30','45','60'},...
    'XTick',[0 90 180 270 360],...
    'FontSize',18,...
    'FontName','arial narrow',...
    'ColorOrder',[0 0.5 0;1 0 0;0 0.75 0.75;0.75 0 0.75;0.75 0.75 0;0.25 0.25 0.25;0 0 1],...
    'Color','none');
%% Uncomment the following line to preserve the X-limits of the axes
xlim(axes2,[0 360]);
%% Uncomment the following line to preserve the Y-limits of the axes
ylim(axes2,[50 100]);
hold(axes2,'all');

% Create plot
plot(X1,Y2,'Parent',axes2,'LineWidth',2);

% Create ylabel
ylabel('Temperature','VerticalAlignment','cap','FontSize',20,...
    'FontName','Arial Narrow',...
    'Color',[0 0.5 0]);

filename = strcat(day,' G_dn and Temp'); 
savefig(filename);
print(filename,'-dpng');


